import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { io } from "socket.io-client"
import QRCode from "qrcode"

export default function Payment() {
  const navigate = useNavigate()
  const [copied, setCopied] = useState(false)
  const [error, setError] = useState(null)
  const [isLoading, setIsLoading] = useState(true)
  const value = "R$ 1,00"
  const description = "Plano de Acompanhamento"
  const [pixCode, setPixCode] = useState("")
  const [paymentStatus, setPaymentStatus] = useState("pending")
  const [socketConnected, setSocketConnected] = useState(false)
  const [qrCodeData, setQrCodeData] = useState(null)
  const [retryCount, setRetryCount] = useState(0)

  useEffect(() => {
    const initializePayment = async () => {
      try {
        setIsLoading(true)
        // Create payment and get PIX code from backend
        const response = await fetch("http://localhost:5000/payments", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            value: 1.00,
            description,
            paymentMethod: "PIX",
            dueDate: new Date().toISOString().split("T")[0],
            customer: "your-customer-id" // Replace with actual customer ID
          }),
        })

        if (!response.ok) {
          const errorData = await response.json()
          throw new Error(errorData.error || "Falha ao gerar pagamento")
        }

        const data = await response.json()
        setPixCode(data.pixCode)
        generateQRCode(data.pixCode)
      } catch (err) {
        console.error("Erro ao inicializar pagamento:", err)
        setError(err.message || "Não foi possível gerar o pagamento. Tente novamente.")
      } finally {
        setIsLoading(false)
      }
    }

    const generateQRCode = async (code) => {
      try {
        const qrCode = await QRCode.toDataURL(code)
        setQrCodeData(qrCode)
      } catch (err) {
        console.error("Erro ao gerar QR Code:", err)
        setError("Não foi possível gerar o QR Code.")
      }
    }

    // Socket connection
    const socket = io("http://localhost:5000", {
      transports: ["websocket"],
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
    })

    socket.on("connect", () => {
      console.log("Socket.IO conectado!")
      setSocketConnected(true)
      setError(null)
    })

    socket.on("connect_error", (err) => {
      console.error("Erro de conexão Socket.IO:", err)
      setSocketConnected(false)
      setError("Erro de conexão com o servidor. Tente novamente mais tarde.")
    })

    socket.on("paymentReceived", (data) => {
      console.log("Evento de pagamento recebido:", data)
      if (data.status === "confirmed") {
        setPaymentStatus("confirmed")
        setError(null)
        setTimeout(() => navigate("/thanks"), 2000)
      }
    })

    socket.on("paymentError", (data) => {
      console.error("Erro no pagamento:", data)
      setPaymentStatus("failed")
      setError(data.message || "Falha no pagamento. Tente novamente.")
      
      // Increment retry count and reinitialize payment if under limit
      if (retryCount < 3) {
        setRetryCount(prev => prev + 1)
        initializePayment()
      }
    })

    initializePayment()

    return () => {
      socket.disconnect()
    }
  }, [navigate, retryCount, description])

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(pixCode)
      setCopied(true)
      setTimeout(() => setCopied(false), 2000)
    } catch (err) {
      console.error("Erro ao copiar:", err)
      setError("Não foi possível copiar o código PIX")
    }
  }

  const handleRetry = () => {
    setError(null)
    setPaymentStatus("pending")
    setRetryCount(0)
    window.location.reload()
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-pink-400 via-purple-400 to-orange-400">
        <div className="w-full max-w-md bg-white rounded-lg shadow-md">
          <div className="p-6">
            <div className="flex flex-col items-center space-y-4">
              <div className="animate-spin h-8 w-8 border-4 border-purple-600 border-t-transparent rounded-full" />
              <p>Gerando pagamento...</p>
            </div>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-r from-pink-400 via-purple-400 to-orange-400">
      <div className="w-full max-w-md bg-white/80 backdrop-blur-sm rounded-lg shadow-md">
        <div className="p-6">
          <h2 className="text-2xl font-semibold text-center mb-6">
            Informações do Pagamento
          </h2>

          {!socketConnected && (
            <div className="mb-4 p-3 bg-yellow-100 text-yellow-700 rounded-md">
              Conectando ao servidor de pagamentos...
            </div>
          )}

          <div className="bg-gray-100 p-4 rounded-lg mb-6">
            <h3 className="text-lg font-medium mb-2">{description}</h3>
            <p className="text-2xl font-bold text-purple-600">{value}</p>
          </div>

          {error ? (
            <div className="mb-6 p-4 bg-red-100 text-red-700 rounded-md">
              <div className="flex items-center mb-2">
                <svg
                  className="w-5 h-5 mr-2"
                  fill="none"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                {error}
              </div>
              <button
                onClick={handleRetry}
                className="text-sm text-red-700 hover:text-red-800 underline"
              >
                Tentar novamente
              </button>
            </div>
          ) : (
            <div className="flex flex-col items-center space-y-4 mb-6">
              {qrCodeData && (
                <img
                  src={qrCodeData || "/placeholder.svg"}
                  alt="QR Code PIX"
                  className="w-64 h-64"
                />
              )}
              <p className="text-sm text-gray-500 text-center">
                Escaneie o QR Code acima com o seu aplicativo bancário para realizar o pagamento via PIX
              </p>
            </div>
          )}

          <button
            onClick={copyToClipboard}
            disabled={!!error || !socketConnected}
            className="w-full flex items-center justify-center py-2 px-4 bg-purple-600 text-white rounded-md hover:bg-purple-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {copied ? (
              <>
                <svg
                  className="w-5 h-5 mr-2"
                  fill="none"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path d="M5 13l4 4L19 7" />
                </svg>
                Copiado!
              </>
            ) : (
              <>
                <svg
                  className="w-5 h-5 mr-2"
                  fill="none"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path d="M8 5H6a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2v-1M8 5a2 2 0 002 2h2a2 2 0 002-2M8 5a2 2 0 012-2h2a2 2 0 012 2m0 0h2a2 2 0 012 2v3m2 4H10m0 0l3-3m-3 3l3 3" />
                </svg>
                Copiar código PIX
              </>
            )}
          </button>

          {!error && (
            <div className="mt-4 text-center">
              <p
                className={`font-semibold ${
                  paymentStatus === "confirmed"
                    ? "text-green-600"
                    : paymentStatus === "failed"
                    ? "text-red-600"
                    : "text-gray-600"
                }`}
              >
                {paymentStatus === "confirmed"
                  ? "Pagamento confirmado!"
                  : paymentStatus === "failed"
                  ? "Pagamento falhou. Tente novamente."
                  : "Aguardando pagamento..."}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}
