import React, { useState, useEffect } from "react"
import { Check, Copy, AlertCircle } from "lucide-react"
import { useNavigate, useLocation } from "react-router-dom"
import { io } from "socket.io-client"
import axios from "axios"
import "./payment.css"

export default function Payment() {
  const navigate = useNavigate()
  const location = useLocation()
  const currentUser = location.state

  if (!currentUser) {
    navigate("/services")
    return null
  }

  const [copied, setCopied] = useState(false)
  const [error, setError] = useState(null)
  const [paymentStatus, setPaymentStatus] = useState("pending")
  const [socketConnected, setSocketConnected] = useState(false)
  const [qrCodeData, setQrCodeData] = useState(null)
  const [pixCode, setPixCode] = useState(null)
  const [qrCodeImage, setQrCodeImage] = useState(null)
  const value = "R$ 1,00"
  const description = "Plano de Acompanhamento"

  useEffect(() => {
    // Gerar o Pix code com a API Asaas
    const generatePixCode = async () => {
      try {
        const response = await axios.post(
          "https://localhost:5000/payments",
          {
            customer: currentUser.userId,
            value: 5.0,
            dueDate: "2025-02-01",
            paymentMethod: "PIX",
            description: "Plano de Acompanhamento",
          },
          {
            headers: {
              "Content-Type": "application/json",
            },
          },
        )

        if (response.status === 200) {
          const { pixQrCode, qrCodeImage } = response.data
          setPixCode(pixQrCode)
          setQrCodeImage(qrCodeImage)
        } else {
          setError("Erro ao gerar o pagamento. Tente novamente.")
        }
      } catch (err) {
        console.error("Erro ao gerar cobrança Pix:", err)
        setError("Não foi possível gerar o código Pix.")
      }
    }

    generatePixCode()

    const socket = io("https://257c-2804-d41-c82e-cb00-d18a-1965-176e-8048.ngrok-free.app", {
      transports: ["websocket", "polling"],
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
    })

    socket.on("connect", () => {
      console.log("Socket.IO conectado!")
      setSocketConnected(true)
      setError(null)
    })

    socket.on("connect_error", (err) => {
      console.error("Erro de conexão Socket.IO:", err)
      setSocketConnected(false)
      setError("Erro de conexão com o servidor. Tentando reconectar...")
    })

    socket.on("disconnect", (reason) => {
      console.log("Socket.IO desconectado:", reason)
      setSocketConnected(false)
      if (reason === "io server disconnect") {
        socket.connect()
      }
    })

    socket.on("paymentReceived", (data) => {
      console.log("Evento de pagamento recebido:", data)
      if (data.status === "confirmed" && data.customer === currentUser.userId) {
        setPaymentStatus("confirmed")
        setError(null)
        // Redireciona automaticamente para a página de agradecimento
        setTimeout(() => navigate("/thanks"), 2000)
      } else if (data.status === "failed" && data.customer === currentUser.userId) {
        setPaymentStatus("failed")
        setError("Pagamento falhou. Tente novamente.")
      }
    })

    return () => {
      socket.disconnect()
    }
  }, [navigate, currentUser.userId])

  const copyToClipboard = async () => {
    if (!pixCode) {
      setError("Código PIX não disponível para cópia.")
      return
    }
    try {
      await navigator.clipboard.writeText(pixCode)
      setCopied(true)
      setTimeout(() => setCopied(false), 2000)
    } catch (err) {
      console.error("Erro ao copiar:", err)
      setError("Não foi possível copiar o código PIX.")
    }
  }

  return (
    <div className="payment-container">
      <div className="payment-card">
        <h2 className="payment-title">Informações do Pagamento</h2>

        {!socketConnected && (
          <div className="error-container">
            <p className="error-text">Conectando ao servidor de pagamentos...</p>
          </div>
        )}

        <div className="payment-content">
          <div className="qr-container">
            <h3 className="payment-description">{description}</h3>
            <p className="payment-value">{value}</p>
            <p className="payment-user">Usuário: {currentUser.name}</p>

            {error ? (
              <div className="error-container">
                <AlertCircle className="error-icon" />
                <p className="error-text">{error}</p>
                <button onClick={() => window.location.reload()} className="retry-button">
                  Tentar novamente
                </button>
              </div>
            ) : (
              <>
                {qrCodeImage ? (
                  <img src={`data:image/png;base64,${qrCodeImage}`} alt="QR Code PIX" className="qr-code" />
                ) : (
                  <p>Carregando QR Code...</p>
                )}
              </>
            )}
          </div>

          <button onClick={copyToClipboard} className="copy-button" disabled={!!error || !socketConnected || !pixCode}>
            {copied ? (
              <>
                <Check size={20} />
                <span>Copiado!</span>
              </>
            ) : (
              <>
                <Copy size={20} />
                <span>Copiar código PIX</span>
              </>
            )}
          </button>

          {!error && (
            <div
              className={`payment-status ${
                paymentStatus === "confirmed"
                  ? "status-confirmed"
                  : paymentStatus === "failed"
                    ? "status-failed"
                    : "status-pending"
              }`}
            >
              {paymentStatus === "confirmed"
                ? "Pagamento confirmado!"
                : paymentStatus === "failed"
                  ? "Pagamento falhou. Tente novamente."
                  : "Aguardando pagamento..."}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}
