import React, { useState, useEffect } from "react";
import { Check, Copy } from 'lucide-react';
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";

export default function Payment() {
  const navigate = useNavigate();
  const [copied, setCopied] = useState(false);
  const [pixCode, setPixCode] = useState('');
  const value = 0.10; // Valor fixo em reais
  const description = "Plano de Acompanhamento";

  useEffect(() => {
    const fetchPixCode = async () => {
      try {
        const response = await fetch('http://localhost:5000/payments', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            customer: 'seu_id_de_cliente_aqui', // Substitua pelo ID do cliente real
            value: value,
            dueDate: new Date().toISOString().split('T')[0], // Data atual
            description: description,
            paymentMethod: 'PIX',
          }),
        });

        if (!response.ok) {
          throw new Error('Falha ao gerar o código PIX');
        }

        const data = await response.json();
        setPixCode(data.encodedImage); // Assumindo que a API retorna o QR code como uma string base64
      } catch (error) {
        console.error('Erro ao gerar o código PIX:', error);
      }
    };

    fetchPixCode();

    // Conectando ao Socket.IO
    const socket = io("https://564b-2804-d41-c82e-cb00-569-9823-69f1-39da.ngrok-free.app");

    socket.on("paymentReceived", (data) => {
      console.log("Evento de pagamento recebido:", data);
      if (data.status === "confirmed") {
        navigate("/thanks");
      }
    });

    // Desconectar ao desmontar o componente
    return () => socket.disconnect();
  }, [navigate, value, description]);

  const copyToClipboard = () => {
    if (pixCode) {
      navigator.clipboard.writeText(pixCode).then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      });
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-r from-pink-400 via-purple-400 to-orange-400">
      <div className="w-full max-w-md bg-white/80 backdrop-blur-sm rounded-3xl shadow-lg p-8">
        <h2 className="text-2xl font-semibold text-center mb-6">Informações do Pagamento</h2>

        <div className="space-y-6">
          <div className="bg-gray-100 p-4 rounded-lg">
            <h3 className="text-lg font-medium mb-2">{description}</h3>
            <p className="text-2xl font-bold text-purple-600">{value}</p>
          </div>

          <div className="flex flex-col items-center space-y-4">
            {pixCode ? (
              <img
                src={`data:image/png;base64,${pixCode}`}
                alt="QR Code PIX"
                className="w-64 h-64"
              />
            ) : (
              <p>Carregando QR Code...</p>
            )}
            <p className="text-sm text-gray-500 text-center">
              Escaneie o QR Code acima com o seu aplicativo bancário para realizar o pagamento via PIX
            </p>
          </div>

          <div className="mt-4">
            <button
              onClick={copyToClipboard}
              className="w-full flex items-center justify-center space-x-2 py-2 px-4 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-200"
            >
              {copied ? (
                <>
                  <Check size={20} />
                  <span>Copiado!</span>
                </>
              ) : (
                <>
                  <Copy size={20} />
                  <span>Copiar código PIX</span>
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
