import React, { useState, useEffect } from "react";
import { Check, Copy, AlertCircle } from 'lucide-react';
import { useNavigate, useLocation } from "react-router-dom";
import { io } from "socket.io-client";
import axios from 'axios';
import './payment.css';

export default function Payment() {
  const navigate = useNavigate();
  const location = useLocation();
  const currentUser = location.state;

  if (!currentUser) {
    navigate("/services");
    return null;
  }

  const [copied, setCopied] = useState(false);
  const [error, setError] = useState(null);
  const [paymentStatus, setPaymentStatus] = useState("pending");
  const [socketConnected, setSocketConnected] = useState(false);
  const [qrCodeData, setQrCodeData] = useState(null);
  const [pixCode, setPixCode] = useState(null);
  const [qrCodeImage, setQrCodeImage] = useState(null); // Added qrCodeImage state
  const value = "R$ 1,00";
  const description = "Plano de Acompanhamento";

  useEffect(() => {
    // Gerar o Pix code com a API Asaas
    const generatePixCode = async () => {
      try {
        const response = await axios.post(
          'https://1fb9-2804-d41-c82e-cb00-659b-c0a3-954e-806e.ngrok-free.app/proxy/cobrancas',
          {
            customer: currentUser.userId,
            value: 5.00,
            dueDate: '2025-02-01',
            billingType: "PIX",
            description: "Plano de Acompanhamento",
            externalReference: "referencia_unica",
          },
          {
            headers: {
              'Content-Type': 'application/json',
              'access_token': '$aact_MzkwODA2MWY2OGM3MWRlMDU2NWM3MzJlNzZmNGZhZGY6OjVkNzVjZmJhLTU3YWEtNGQ0YS05NjkxLWM1MDkwMmE3ZTFhODo6JGFhY2hfYzU4MmU4NWYtNmZlOS00ODQ2LTkzNTMtNzUxNTZkNmNjYzM2',
            }
          }
        );
    
        if (response.status === 201 || response.status === 200) {
          const { pixQrCode, qrCodeImage, invoiceUrl } = response.data;
    
          if (pixQrCode) {
            setPixCode(pixQrCode);
          } else if (invoiceUrl) {
            setQrCodeData(invoiceUrl); // Fallback para o link da fatura
          }
    
          if (qrCodeImage) {
            setQrCodeImage(qrCodeImage);
          } else {
            setError("QR Code não disponível.");
          }
        } else {
          setError("Erro ao gerar o pagamento. Tente novamente.");
        }
      } catch (err) {
        console.error("Erro ao gerar cobrança Pix:", err);
        setError("Não foi possível gerar o código Pix.");
      }
   
    
    };

    generatePixCode();

    const socket = io("https://1fb9-2804-d41-c82e-cb00-659b-c0a3-954e-806e.ngrok-free.app", {
      transports: ['websocket', 'polling'],
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
    });

    socket.on("connect", () => {
      console.log("Socket.IO conectado!");
      setSocketConnected(true);
      setError(null);
    });

    socket.on("connect_error", (err) => {
      console.error("Erro de conexão Socket.IO:", err);
      setSocketConnected(false);
      setError("Erro de conexão com o servidor. Tentando reconectar...");
    });

    socket.on("disconnect", (reason) => {
      console.log("Socket.IO desconectado:", reason);
      setSocketConnected(false);
      if (reason === "io server disconnect") {
        socket.connect();
      }
    });

    socket.on("paymentReceived", (data) => {
      console.log("Evento de pagamento recebido:", data);
      if (data.status === "confirmed" && data.userId === currentUser.userId) {
        setPaymentStatus("confirmed");
        setError(null);
        setTimeout(() => navigate("/thanks"), 2000);
      } else if (data.status === "failed" && data.userId === currentUser.userId) {
        setPaymentStatus("failed");
        setError("Pagamento falhou. Tente novamente.");
      }
    });

    return () => {
      socket.disconnect();
    };
  }, [navigate, currentUser.userId]);

  const copyToClipboard = async () => {
    if (!pixCode) {
      setError("Código PIX não disponível para cópia.");
      return;
    }
    try {
      await navigator.clipboard.writeText(pixCode);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error("Erro ao copiar:", err);
      setError("Não foi possível copiar o código PIX.");
    }
  };
  

  return (
    <div className="payment-container">
      <div className="payment-card">
        <h2 className="payment-title">Informações do Pagamento</h2>

        {!socketConnected && (
          <div className="error-container">
            <p className="error-text">
              Conectando ao servidor de pagamentos...
            </p>
          </div>
        )}

        <div className="payment-content">
          <div className="qr-container">
            <h3 className="payment-description">{description}</h3>
            <p className="payment-value">{value}</p>
            <p className="payment-user">Usuário: {currentUser.name}</p>

            {error ? (
              <div className="error-container">
                <AlertCircle className="error-icon" />
                <p className="error-text">{error}</p>
                <button
                  onClick={() => window.location.reload()}
                  className="retry-button"
                >
                  Tentar novamente
                </button>
              </div>
            ) : (
              <>
                {qrCodeImage ? (
  <img src={qrCodeImage} alt="QR Code PIX" className="qr-code" />
) : qrCodeData ? (
  <a href={qrCodeData} target="_blank" rel="noopener noreferrer" className="invoice-link">
    Clique aqui para acessar a página de pagamento
  </a>
) : (
  <p>Carregando QR Code...</p>
)}

                <p className="payment-description">
                  Escaneie o QR Code acima com o seu aplicativo bancário para realizar o pagamento via PIX
                </p>
              </>
            )}
          </div>

          <button
            onClick={copyToClipboard}
            className="copy-button"
            disabled={!!error || !socketConnected}
          >
            {copied ? (
              <>
                <Check size={20} />
                <span>Copiado!</span>
              </>
            ) : (
              <>
                <Copy size={20} />
                <span>Copiar código PIX</span>
              </>
            )}
          </button>

          {!error && (
            <div className={`payment-status ${
              paymentStatus === "confirmed" 
                ? "status-confirmed" 
                : paymentStatus === "failed" 
                ? "status-failed" 
                : "status-pending"
            }`}>
              {paymentStatus === "confirmed" ? "Pagamento confirmado!" : 
               paymentStatus === "failed" ? "Pagamento falhou. Tente novamente." : 
               "Aguardando pagamento..."}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
