import React, { useState, useEffect } from "react"
import { Check, Copy, AlertCircle, Mail, User, Phone, CreditCard } from "lucide-react"
import { useNavigate } from "react-router-dom"
import { io } from "socket.io-client"
import axios from "axios"
import InputMask from "react-input-mask"
import "./payment.css"

const API_URL = "https://aasaasteste-production.up.railway.app"

export default function Payment() {
  const navigate = useNavigate()
  const [nome, setNome] = useState("")
  const [email, setEmail] = useState("")
  const [telefone, setTelefone] = useState("")
  const [cpfCnpj, setCpfCnpj] = useState("")
  const [copied, setCopied] = useState(false)
  const [error, setError] = useState(null)
  const [paymentStatus, setPaymentStatus] = useState("pending")
  const [socketConnected, setSocketConnected] = useState(false)
  const [qrCodeData, setQrCodeData] = useState(null)
  const [pixCode, setPixCode] = useState(null)
  const [qrCodeImage, setQrCodeImage] = useState(null)
  const [isLoading, setIsLoading] = useState(false)
  const [showPaymentInfo, setShowPaymentInfo] = useState(false)
  const value = "R$ 5,00"
  const description = "Plano de Acompanhamento"

  useEffect(() => {
    const socket = io(API_URL, {
      transports: ["websocket", "polling"],
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
    })

    socket.on("connect", () => {
      console.log("Socket.IO conectado!")
      setSocketConnected(true)
      setError(null)
    })

    socket.on("connect_error", (err) => {
      console.error("Erro de conexão Socket.IO:", err)
      setSocketConnected(false)
      setError("Erro de conexão com o servidor. Tentando reconectar...")
    })

    socket.on("disconnect", (reason) => {
      console.log("Socket.IO desconectado:", reason)
      setSocketConnected(false)
      if (reason === "io server disconnect") {
        socket.connect()
      }
    })

    socket.on("paymentReceived", (data) => {
      console.log("Evento de pagamento recebido:", data)
      if (data.status === "confirmed") {
        setPaymentStatus("confirmed")
        setError(null)
        setTimeout(() => navigate("/thanks"), 2000)
      } else if (data.status === "failed") {
        setPaymentStatus("failed")
        setError("Pagamento falhou. Tente novamente.")
      }
    })

    return () => {
      socket.disconnect()
    }
  }, [navigate])

  const handleSubmit = async (event) => {
    event.preventDefault()
    setIsLoading(true)
    setError(null)

    try {
      // Create customer
      const customerResponse = await axios.post(`${API_URL}/customers`, {
        name: nome,
        email: email,
        phone: telefone,
        cpfCnpj: cpfCnpj,
      })

      if (customerResponse.status !== 201) {
        throw new Error("Erro ao criar o cliente.")
      }

      const customerId = customerResponse.data.id

      // Generate payment
      const paymentResponse = await axios.post(
        `${API_URL}/payments`,
        {
          customer: customerId,
          value: 5.0,
          dueDate: "2025-02-01",
          paymentMethod: "PIX",
          description: "Plano de Acompanhamento",
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        },
      )

      if (paymentResponse.status === 200) {
        const { pixQrCode, qrCodeImage } = paymentResponse.data
        setPixCode(pixQrCode)
        setQrCodeImage(qrCodeImage)
        setShowPaymentInfo(true)
      } else {
        throw new Error("Erro ao gerar o pagamento.")
      }
    } catch (error) {
      console.error("Erro:", error.response?.data || error.message)
      setError("Ocorreu um erro. Por favor, tente novamente.")
    } finally {
      setIsLoading(false)
    }
  }

  const copyToClipboard = async () => {
    if (!pixCode) {
      setError("Código PIX não disponível para cópia.")
      return
    }
    try {
      await navigator.clipboard.writeText(pixCode)
      setCopied(true)
      setTimeout(() => setCopied(false), 2000)
    } catch (err) {
      console.error("Erro ao copiar:", err)
      setError("Não foi possível copiar o código PIX.")
    }
  }

  return (
    <div className="payment-container">
      <div className="payment-card">
        <h2 className="payment-title">{showPaymentInfo ? "Informações do Pagamento" : "Faça Seu Cadastro!"}</h2>

        {!showPaymentInfo ? (
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="relative">
              <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <input
                type="text"
                value={nome}
                onChange={(e) => setNome(e.target.value)}
                required
                placeholder="Nome"
                className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
              />
            </div>
            <div className="relative">
              <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                placeholder="Email"
                className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
              />
            </div>
            <div className="relative">
              <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <InputMask
                mask="(99) 99999-9999"
                value={telefone}
                onChange={(e) => setTelefone(e.target.value)}
                required
                placeholder="Celular"
              >
                {(inputProps) => (
                  <input
                    {...inputProps}
                    type="tel"
                    className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
                  />
                )}
              </InputMask>
            </div>
            <div className="relative">
              <CreditCard className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <InputMask
                mask="999.999.999-99"
                value={cpfCnpj}
                onChange={(e) => setCpfCnpj(e.target.value)}
                required
                placeholder="CPF"
              >
                {(inputProps) => (
                  <input
                    {...inputProps}
                    type="text"
                    className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
                  />
                )}
              </InputMask>
            </div>
            <button
              type="submit"
              disabled={isLoading}
              className="w-full py-3 px-6 text-white font-medium bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl hover:opacity-90 transition-opacity disabled:opacity-50 submit-button"
            >
              {isLoading ? "Processando..." : "Gerar Pagamento"}
            </button>
          </form>
        ) : (
          <div className="payment-content">
            <div className="qr-container">
              <h3 className="payment-description">{description}</h3>
              <p className="payment-value">{value}</p>
              <p className="payment-user">Usuário: {nome}</p>

              {error ? (
                <div className="error-container">
                  <AlertCircle className="error-icon" />
                  <p className="error-text">{error}</p>
                  <button onClick={() => window.location.reload()} className="retry-button">
                    Tentar novamente
                  </button>
                </div>
              ) : (
                <>
                  {qrCodeImage ? (
                    <img
                      src={qrCodeImage || "/placeholder.svg"}
                      alt="QR Code PIX"
                      className="qr-code"
                      style={{ maxWidth: "200px", width: "100%", height: "auto" }}
                    />
                  ) : (
                    <p>Carregando QR Code...</p>
                  )}
                </>
              )}
            </div>

            <button
              onClick={copyToClipboard}
              className="copy-button"
              disabled={!!error || !socketConnected || !pixCode}
            >
              {copied ? (
                <>
                  <Check size={20} />
                  <span>Copiado!</span>
                </>
              ) : (
                <>
                  <Copy size={20} />
                  <span>Copiar código PIX</span>
                </>
              )}
            </button>

            {!error && (
              <div
                className={`payment-status ${
                  paymentStatus === "confirmed"
                    ? "status-confirmed"
                    : paymentStatus === "failed"
                      ? "status-failed"
                      : "status-pending"
                }`}
              >
                {paymentStatus === "confirmed"
                  ? "Pagamento confirmado!"
                  : paymentStatus === "failed"
                    ? "Pagamento falhou. Tente novamente."
                    : "Aguardando pagamento..."}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  )
}
