import React, { useState, useEffect } from "react"
import { Check, Copy, AlertCircle, User, Mail } from "lucide-react"
import { useNavigate } from "react-router-dom"
import { io } from "socket.io-client"
import axios from "axios"

const API_URL = "http://localhost:5000"

export default function Payment() {
  const navigate = useNavigate()
  const [error, setError] = useState(null)
  const [paymentStatus, setPaymentStatus] = useState("pending")
  const [socketConnected, setSocketConnected] = useState(false)
  const [pixCode, setPixCode] = useState(null)
  const [qrCodeImage, setQrCodeImage] = useState(null)
  const [isLoading, setIsLoading] = useState(false)
  const [copied, setCopied] = useState(false)
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [showForm, setShowForm] = useState(true)
  const value = "R$ 5,00"
  const description = "Plano de Acompanhamento"

  useEffect(() => {
    const socket = io(API_URL, {
      transports: ["websocket", "polling"],
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
    })

    socket.on("connect", () => {
      console.log("Socket.IO conectado!")
      setSocketConnected(true)
      setError(null)
    })

    socket.on("connect_error", (err) => {
      console.error("Erro de conexão Socket.IO:", err)
      setSocketConnected(false)
      setError("Erro de conexão com o servidor. Tentando reconectar...")
    })

    socket.on("disconnect", (reason) => {
      console.log("Socket.IO desconectado:", reason)
      setSocketConnected(false)
      if (reason === "io server disconnect") {
        socket.connect()
      }
    })

    socket.on("paymentReceived", (data) => {
      console.log("Evento de pagamento recebido:", data)
      if (data.status === "confirmed") {
        setPaymentStatus("confirmed")
        setError(null)
        setTimeout(() => navigate("/thanks"), 2000)
      } else if (data.status === "failed") {
        setPaymentStatus("failed")
        setError("Pagamento falhou. Tente novamente.")
      }
    })

    return () => {
      socket.disconnect()
    }
  }, [navigate])

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!name || !email) {
      setError("Por favor, preencha todos os campos.")
      return
    }
    setShowForm(false)
    await generatePayment()
  }

  const generatePayment = async () => {
    setIsLoading(true)
    setError(null)

    try {
      const paymentResponse = await axios.post(
        `${API_URL}/payments`,
        {
          name,
          email,
          value: 5.0,
          dueDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split("T")[0],
          description: "Plano de Acompanhamento",
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        },
      )

      console.log("Payment response:", paymentResponse)

      if (paymentResponse.status === 200 && paymentResponse.data) {
        const { pixQrCode, qrCodeImage } = paymentResponse.data
        setPixCode(pixQrCode)
        setQrCodeImage(qrCodeImage)
      } else {
        throw new Error("Resposta inesperada do servidor")
      }
    } catch (error) {
      console.error("Erro detalhado:", error.response?.data || error.message)
      setError(error.response?.data?.error || "Ocorreu um erro ao gerar o pagamento. Por favor, tente novamente.")
      setShowForm(true)
    } finally {
      setIsLoading(false)
    }
  }

  const copyToClipboard = async () => {
    if (!pixCode) {
      setError("Código PIX não disponível para cópia.")
      return
    }
    try {
      await navigator.clipboard.writeText(pixCode)
      setCopied(true)
      setTimeout(() => setCopied(false), 2000)
    } catch (err) {
      console.error("Erro ao copiar:", err)
      setError("Não foi possível copiar o código PIX.")
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white rounded-xl shadow-lg p-8 max-w-md w-full">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Pagamento PIX</h2>
        <div className="space-y-6">
          <div className="text-center">
            <h3 className="text-xl font-medium text-gray-700 mb-2">{description}</h3>
            <p className="text-4xl font-bold text-green-600">{value}</p>
          </div>

          {error && (
            <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-md" role="alert">
              <div className="flex">
                <div className="flex-shrink-0">
                  <AlertCircle className="h-5 w-5 text-red-500" aria-hidden="true" />
                </div>
                <div className="ml-3">
                  <p className="text-sm">{error}</p>
                </div>
              </div>
            </div>
          )}

          {showForm ? (
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">
                  Nome
                </label>
                <div className="mt-1 relative rounded-md shadow-sm">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <User className="h-5 w-5 text-gray-400" aria-hidden="true" />
                  </div>
                  <input
                    type="text"
                    id="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                    placeholder="Seu nome completo"
                  />
                </div>
              </div>
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                  Email
                </label>
                <div className="mt-1 relative rounded-md shadow-sm">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Mail className="h-5 w-5 text-gray-400" aria-hidden="true" />
                  </div>
                  <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                    placeholder="seu@email.com"
                  />
                </div>
              </div>
              <button
                type="submit"
                className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 ease-in-out"
                disabled={isLoading}
              >
                {isLoading ? "Processando..." : "Gerar Pagamento PIX"}
              </button>
            </form>
          ) : (
            <div className="space-y-6">
              {qrCodeImage ? (
                <div className="flex flex-col items-center">
                  <img src={qrCodeImage || "/placeholder.svg"} alt="QR Code PIX" className="w-48 h-48 mb-4" />
                  <p className="text-sm text-gray-600 text-center">
                    Escaneie o QR Code acima com o aplicativo do seu banco para realizar o pagamento via PIX.
                  </p>
                </div>
              ) : (
                <p className="text-center text-gray-600">Gerando QR Code...</p>
              )}

              {pixCode && (
                <button
                  onClick={copyToClipboard}
                  className="w-full flex items-center justify-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 transition duration-150 ease-in-out"
                  disabled={!!error || !socketConnected}
                >
                  {copied ? (
                    <>
                      <Check className="mr-2 h-5 w-5" aria-hidden="true" />
                      Copiado!
                    </>
                  ) : (
                    <>
                      <Copy className="mr-2 h-5 w-5" aria-hidden="true" />
                      Copiar código PIX
                    </>
                  )}
                </button>
              )}

              <div
                className={`text-center text-sm font-medium p-2 rounded-md ${
                  paymentStatus === "confirmed"
                    ? "bg-green-100 text-green-800"
                    : paymentStatus === "failed"
                      ? "bg-red-100 text-red-800"
                      : "bg-yellow-100 text-yellow-800"
                }`}
              >
                {paymentStatus === "confirmed"
                  ? "Pagamento confirmado!"
                  : paymentStatus === "failed"
                    ? "Pagamento falhou. Tente novamente."
                    : "Aguardando pagamento..."}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}
