'use client'

import { useState } from "react"
import { Mail, User, Phone, CreditCard } from 'lucide-react'
import axios from "axios"
import './services.css'

axios.defaults.baseURL = 'http://localhost:5000';

const PIX_KEY = '390cd139-779a-4e86-950d-8d2db758bcb4';

export default function Services() {
  const [nome, setNome] = useState("")
  const [email, setEmail] = useState("")
  const [telefone, setTelefone] = useState("")
  const [cpfCnpj, setCpfCnpj] = useState("")
  const [isLoading, setIsLoading] = useState(false)
  const [pixUrl, setPixUrl] = useState("")
  const [error, setError] = useState("")

  const valor = 10.0

  const handleSubmit = async (event) => {
    event.preventDefault()
    setIsLoading(true)
    setError("")
    try {
      console.log("Creating customer...");
      const clienteResponse = await axios.post("/customers", {
        name: nome,
        email: email,
        phone: telefone,
        cpfCnpj: cpfCnpj,
      })

      const clienteId = clienteResponse.data.id

      if (!clienteId) {
        throw new Error("Falha ao criar cliente")
      }

      console.log("Customer created successfully. Creating payment...");
      const cobrancaResponse = await axios.post("/payments", {
        customer: clienteId,
        value: valor.toFixed(2),
        dueDate: new Date().toISOString().split("T")[0],
        description: "Pagamento via Pix",
        paymentMethod: "PIX",
        pixKey: PIX_KEY,
      });

      console.log("Payment response:", cobrancaResponse.data);

      if (cobrancaResponse.data.qrCode) {
        setPixUrl(cobrancaResponse.data.qrCode)
        alert("Cobrança criada com sucesso!")
      } else {
        throw new Error("QR Code não gerado")
      }
    } catch (error) {
      console.error("Erro ao processar solicitação:", error.response?.data || error.message);
      if (error.response) {
        const errorMessage = error.response.data.error[0]?.description || error.response.data.error || 'Ocorreu um erro desconhecido.';
        setError(`Erro do servidor: ${errorMessage}`);
        console.error("Detalhes do erro:", error.response.data);
      } else if (error.request) {
        setError("Não foi possível conectar ao servidor. Verifique sua conexão e tente novamente.");
      } else {
        setError("Ocorreu um erro ao processar sua solicitação. Por favor, tente novamente.");
      }
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-r from-pink-400 via-purple-400 to-orange-400 services-container">
      <div className="w-full max-w-md bg-white/80 backdrop-blur-sm rounded-3xl shadow-lg p-8 form-container">
        <h2 className="text-2xl font-semibold text-center mb-8 text-gray-800">Faça Seu Cadastro!</h2>
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="relative">
            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
            <input
              type="text"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              required
              placeholder="Nome"
              className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
            />
          </div>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Email"
              className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
            />
          </div>
          <div className="relative">
            <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
            <input
              type="tel"
              value={telefone}
              onChange={(e) => setTelefone(e.target.value)}
              required
              placeholder="Celular"
              className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
            />
          </div>
          <div className="relative">
            <CreditCard className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
            <input
              type="text"
              value={cpfCnpj}
              onChange={(e) => setCpfCnpj(e.target.value)}
              required
              placeholder="CPF ou CNPJ"
              className="w-full pl-12 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent input-field"
            />
          </div>
          <button
            type="submit"
            disabled={isLoading}
            className="w-full py-3 px-6 text-white font-medium bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl hover:opacity-90 transition-opacity disabled:opacity-50 submit-button"
          >
            {isLoading ? "Processing..." : "Send"}
          </button>
        </form>

        {pixUrl ? (
          <div className="mt-8 text-center qr-code-container">
            <h3 className="text-lg font-semibold mb-4">QR Code Pix Gerado:</h3>
            <img src={pixUrl || "/placeholder.svg"} alt="QR Code Pix" className="mx-auto mb-4 qr-code-image" onError={(e) => {
              e.target.onerror = null;
              e.target.src = "/placeholder.svg";
              setError("Erro ao carregar o QR Code. Por favor, tente novamente.");
            }} />
            <p className="text-gray-600">Escaneie o código acima para efetuar o pagamento.</p>
          </div>
        ) : error ? (
          <p className="mt-8 text-center text-red-500">{error}</p>
        ) : null}
      </div>
    </div>
  )
}
