import React, { useState, useEffect } from "react";
import { Check, Copy, AlertCircle } from 'lucide-react';
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import QRCode from 'qrcode'; // Importando a biblioteca para gerar QR Code

export default function Payment({ currentUser }) {
  const navigate = useNavigate();
  const [copied, setCopied] = useState(false);
  const [error, setError] = useState(null);
  const [paymentStatus, setPaymentStatus] = useState("pending");
  const [socketConnected, setSocketConnected] = useState(false);
  const [qrCodeData, setQrCodeData] = useState(null); // Estado para armazenar o QR Code
  const value = "R$ 1,00";
  const description = "Plano de Acompanhamento";
  const pixCode = "00020126580014BR.GOV.BCB.PIX013626257957-02ec-43f9-9768-fcafec99ae0b52040000530398654041.005802BR5901N6001C62150511TestCompany630422F0";

  useEffect(() => {
    // Gerar o QR Code assim que o componente for carregado
    const generateQRCode = async () => {
      try {
        const qrCode = await QRCode.toDataURL(pixCode); // Gerando QR Code a partir da chave Pix
        setQrCodeData(qrCode); // Atualizando o estado com o QR Code gerado
      } catch (err) {
        console.error("Erro ao gerar QR Code:", err);
        setError("Não foi possível gerar o QR Code.");
      }
    };

    generateQRCode();

    // Conectar ao socket e outras lógicas
    const socket = io("http://localhost:5000", {
      transports: ['websocket', 'polling'],
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
    });

    socket.on("connect", () => {
      console.log("Socket.IO conectado!");
      setSocketConnected(true);
      setError(null);
    });

    socket.on("connect_error", (err) => {
      console.error("Erro de conexão Socket.IO:", err);
      setSocketConnected(false);
      setError("Erro de conexão com o servidor. Tentando reconectar...");
    });

    socket.on("disconnect", (reason) => {
      console.log("Socket.IO desconectado:", reason);
      setSocketConnected(false);
      if (reason === "io server disconnect") {
        socket.connect();
      }
    });

    socket.on("paymentReceived", (data) => {
      console.log("Evento de pagamento recebido:", data);
      if (data.status === "confirmed" && data.userId === currentUser.id) {
        setPaymentStatus("confirmed");
        setError(null);
        setTimeout(() => navigate("/thanks"), 2000);
      } else if (data.status === "failed" && data.userId === currentUser.id) {
        setPaymentStatus("failed");
        setError("Pagamento falhou. Tente novamente.");
      }
    });

    return () => {
      socket.disconnect();
    };
  }, [navigate, currentUser.id]);

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(pixCode);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error("Erro ao copiar:", err);
      setError("Não foi possível copiar o código PIX");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-r from-pink-400 via-purple-400 to-orange-400">
      <div className="w-full max-w-md bg-white/80 backdrop-blur-sm rounded-3xl shadow-lg p-8">
        <h2 className="text-2xl font-semibold text-center mb-6">Informações do Pagamento</h2>

        {!socketConnected && (
          <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
            <p className="text-yellow-700 text-sm text-center">
              Conectando ao servidor de pagamentos...
            </p>
          </div>
        )}

        <div className="space-y-6">
          <div className="bg-gray-100 p-4 rounded-lg">
            <h3 className="text-lg font-medium mb-2">{description}</h3>
            <p className="text-2xl font-bold text-purple-600">{value}</p>
            <p className="text-sm text-gray-500">Usuário: {currentUser.name}</p>
          </div>

          {error ? (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-center">
              <AlertCircle className="mx-auto h-6 w-6 text-red-500 mb-2" />
              <p className="text-red-600 text-sm">{error}</p>
              <button
                onClick={() => window.location.reload()}
                className="mt-3 text-sm text-red-600 hover:text-red-700 underline"
              >
                Tentar novamente
              </button>
            </div>
          ) : (
            <div className="flex flex-col items-center space-y-4">
              {qrCodeData ? (
                <img
                  src={qrCodeData}  // Usando o QR Code gerado
                  alt="QR Code PIX"
                  className="w-64 h-64"
                />
              ) : (
                <p>Carregando QR Code...</p>
              )}
              <p className="text-sm text-gray-500 text-center">
                Escaneie o QR Code acima com o seu aplicativo bancário para realizar o pagamento via PIX
              </p>
            </div>
          )}

          <div className="mt-4">
            <button
              onClick={copyToClipboard}
              className="w-full flex items-center justify-center space-x-2 py-2 px-4 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-200"
              disabled={!!error || !socketConnected}
            >
              {copied ? (
                <>
                  <Check size={20} />
                  <span>Copiado!</span>
                </>
              ) : (
                <>
                  <Copy size={20} />
                  <span>Copiar código PIX</span>
                </>
              )}
            </button>
          </div>

          {!error && (
            <div className="mt-4 text-center">
              <p className={`font-semibold ${paymentStatus === "confirmed" ? "text-green-600" : paymentStatus === "failed" ? "text-red-600" : "text-gray-600"}`}>
                {paymentStatus === "confirmed" ? "Pagamento confirmado!" : 
                 paymentStatus === "failed" ? "Pagamento falhou. Tente novamente." : 
                 "Aguardando pagamento..."}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
