import React, { useState, useEffect } from 'react'
import { Check, Copy } from 'lucide-react'
import { useNavigate } from 'react-router-dom'

export default function Payment() {
  const navigate = useNavigate()
  const [copied, setCopied] = useState(false)
  const value = "R$ 0,10"
  const description = "Plano de Acompanhamento"
  const pixCode = "00020126580014br.gov.bcb.pix0136390cd139-779a-4e86-950d-8d2db758bcb40222Pagamento do Produto XYZ5204000053039865802BR5913Fulano de Tal6008BRASILIA62070503***63041D3D"

  useEffect(() => {
    const checkPaymentStatus = async () => {
      try {
        const response = await fetch('/api/check-payment-status');
        const data = await response.json();
        
        if (data.status === 'confirmed') {
          navigate('/thanks');
        }
      } catch (error) {
        console.error('Error checking payment status:', error);
      }
    };

    // Check every 5 seconds
    const interval = setInterval(checkPaymentStatus, 5000);

    return () => clearInterval(interval);
  }, [navigate]);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(pixCode).then(() => {
      setCopied(true)
      setTimeout(() => setCopied(false), 2000)
    })
  }

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-r from-pink-400 via-purple-400 to-orange-400">
      <div className="w-full max-w-md bg-white/80 backdrop-blur-sm rounded-3xl shadow-lg p-8">
        <h2 className="text-2xl font-semibold text-center mb-6">Informações do Pagamento</h2>
        
        <div className="space-y-6">
          <div className="bg-gray-100 p-4 rounded-lg">
            <h3 className="text-lg font-medium mb-2">{description}</h3>
            <p className="text-2xl font-bold text-purple-600">{value}</p>
          </div>
          
          <div className="flex flex-col items-center space-y-4">
            <img 
              src="https://hebbkx1anhila5yf.public.blob.vercel-storage.com/qrcode-pix%20(2)-IoWiTgYNrWX9Nt4mviSfOKd6vB4vH9.png"
              alt="QR Code PIX"
              className="w-64 h-64"
            />
            <p className="text-sm text-gray-500 text-center">
              Escaneie o QR Code acima com o seu aplicativo bancário para realizar o pagamento via PIX
            </p>
          </div>

          <div className="mt-4">
            <button
              onClick={copyToClipboard}
              className="w-full flex items-center justify-center space-x-2 py-2 px-4 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-200"
            >
              {copied ? (
                <>
                  <Check size={20} />
                  <span>Copiado!</span>
                </>
              ) : (
                <>
                  <Copy size={20} />
                  <span>Copiar código PIX</span>
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}
