'use client'

import React, { useState, useEffect, useCallback } from 'react';
import { Check, Copy, AlertCircle } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const BACKEND_URL = 'https://localhost:5000';

export default function Payment() {
  const navigate = useNavigate();
  const [paymentDetails, setPaymentDetails] = useState(null);
  const [error, setError] = useState(null);
  const [copied, setCopied] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState('pending');
  const [socketConnected, setSocketConnected] = useState(false);
  const [ws, setWs] = useState(null);

  // WebSocket connection handler
  const connectWebSocket = useCallback(() => {
    try {
      // Create WebSocket connection
      const wsUrl = `${BACKEND_URL.replace('https://', 'wss://').replace('http://', 'ws://')}`;
      console.log('Connecting to WebSocket:', wsUrl);
      
      const socket = new WebSocket(wsUrl);

      socket.onopen = () => {
        console.log('WebSocket connected');
        setSocketConnected(true);
        setError(null);
      };

      socket.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);
          console.log('WebSocket message:', data);
          
          if (data.type === 'paymentUpdate') {
            setPaymentStatus(data.payment.status);
            if (data.payment.status === 'confirmed') {
              setTimeout(() => navigate('/thanks'), 2000);
            }
          }
        } catch (err) {
          console.error('Error parsing WebSocket message:', err);
        }
      };

      socket.onclose = () => {
        console.log('WebSocket disconnected');
        setSocketConnected(false);
        // Attempt to reconnect after 5 seconds
        setTimeout(connectWebSocket, 5000);
      };

      socket.onerror = (error) => {
        console.error('WebSocket error:', error);
        setSocketConnected(false);
        setError('Erro de conexão com o servidor. Tentando reconectar...');
      };

      setWs(socket);

      return () => {
        if (socket) {
          socket.close();
        }
      };
    } catch (error) {
      console.error('Error creating WebSocket:', error);
      setError('Erro ao criar conexão com o servidor');
    }
  }, [navigate]);

  // Initialize WebSocket connection
  useEffect(() => {
    const cleanup = connectWebSocket();
    return () => {
      cleanup();
    };
  }, [connectWebSocket]);

  const fetchPaymentDetails = async () => {
    try {
      const response = await fetch(`${BACKEND_URL}/generate-pix`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        throw new TypeError("Oops, we haven't got JSON!");
      }
      const data = await response.json();
      console.log('Payment details:', data);
      setPaymentDetails(data);
    } catch (error) {
      console.error("Erro ao buscar detalhes do pagamento:", error);
      setError(`Não foi possível carregar os detalhes do pagamento: ${error.message}`);
    }
  };

  useEffect(() => {
    fetchPaymentDetails();
  }, []);

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(paymentDetails.pixCode);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error("Erro ao copiar:", err);
      setError("Não foi possível copiar o código PIX");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-r from-pink-400 via-purple-400 to-orange-400">
      <div className="w-full max-w-md bg-white/80 backdrop-blur-sm rounded-3xl shadow-lg p-8">
        <h2 className="text-2xl font-semibold text-center mb-6">Informações do Pagamento</h2>

        {!socketConnected && (
          <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
            <p className="text-yellow-700 text-sm text-center">
              {error || 'Conectando ao servidor de pagamentos...'}
            </p>
          </div>
        )}

        <div className="space-y-6">
          <div className="bg-gray-100 p-4 rounded-lg">
            <h3 className="text-lg font-medium mb-2">{paymentDetails?.description || 'Carregando...'}</h3>
            <p className="text-2xl font-bold text-purple-600">{paymentDetails?.value || 'Carregando...'}</p>
          </div>

          {error ? (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-center">
              <AlertCircle className="mx-auto h-6 w-6 text-red-500 mb-2" />
              <p className="text-red-600 text-sm">{error}</p>
              <button
                onClick={() => window.location.reload()}
                className="mt-3 text-sm text-red-600 hover:text-red-700 underline"
              >
                Tentar novamente
              </button>
            </div>
          ) : (
            <div className="flex flex-col items-center space-y-4">
              {paymentDetails?.qrCodeUrl ? (
                <img
                  src={paymentDetails.qrCodeUrl || "/placeholder.svg"}
                  alt="QR Code PIX"
                  className="w-64 h-64"
                />
              ) : (
                <p>Carregando QR Code...</p>
              )}
              <p className="text-sm text-gray-500 text-center">
                Escaneie o QR Code acima com o seu aplicativo bancário para realizar o pagamento via PIX
              </p>
            </div>
          )}

          <div className="mt-4">
            <button
              onClick={copyToClipboard}
              className="w-full flex items-center justify-center space-x-2 py-2 px-4 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-200"
              disabled={!paymentDetails?.pixCode || !!error}
            >
              {copied ? (
                <>
                  <Check size={20} />
                  <span>Copiado!</span>
                </>
              ) : (
                <>
                  <Copy size={20} />
                  <span>Copiar código PIX</span>
                </>
              )}
            </button>
          </div>

          {!error && (
            <div className="mt-4 text-center">
              <p className={`font-semibold ${
                paymentStatus === "confirmed" ? "text-green-600" : 
                paymentStatus === "failed" ? "text-red-600" : 
                "text-gray-600"
              }`}>
                {paymentStatus === "confirmed" ? "Pagamento confirmado!" : 
                 paymentStatus === "failed" ? "Pagamento falhou. Tente novamente." : 
                 "Aguardando pagamento..."}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
